import  { useEffect } from 'react'
import {  useDispatch,useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { deleteInfluencer, getInfluenceri, logoutAction } from '../action/influencerActions';
import EditProfile from '../component/EditProfile';
import './profile.css';


const Profile = () => {
  const dispatch = useDispatch();
  const nav = useNavigate();

   const {isAuth,} =  useSelector(state=>state.influencer) 

//  const {influencerInfo} = useSelector((state)=>state.influencer); 
    useEffect(()=> {
    dispatch(getInfluenceri())
  },[]);
  const handleDelete = (inflId) => { 
    dispatch(deleteInfluencer(inflId))
   };

  const logout = (e) => { 
    e.preventDefault();
    dispatch(logoutAction())
   }
  useEffect(()=>{
    if (! isAuth) nav('/logininfluencer') 
        }, [isAuth,nav]);
  return (
    <section className='proo'>

    <div className='wrapper'>
    <div className='profileCard'>
      <div className='profileCardImg'>
       <img src='https://images.ctfassets.net/hrltx12pl8hq/3j5RylRv1ZdswxcBaMi0y7/b84fa97296bd2350db6ea194c0dce7db/Music_Icon.jpg' alt="profile"/> 
      </div>
      
   
   
   
       <div className='profileCardCnt'>
       <div className='profileCardName'>Shakti Mohan</div>
       <div className='profileCardTxt'>Indian Dancer,<strong>Television Personality</strong></div>
       <div className='profileCardLoc'>
       
        <span className='profileCardLocTxt'>
         Mumbai, India
        </span>
       </div>
  
       <div className='profile-card-inf'>
        <div className='profile-card-inf__item'>
         <div className='profile-card-inf__title'>5M+</div>
         <div className='profile-card-inf__txt'>Followers</div>
        </div>
       
        <div className='profile-card-inf__item'>
         <div className='profile-card-inf__title'>123</div>
         <div className='profile-card-inf__txt'>Posts</div>
        </div>
        <div className='profile-card-inf__item'>
         <div className='profile-card-inf__title'>9714</div>
         <div className='profile-card-inf__txt'> average_comments</div>
        </div>
        <div className='profile-card-inf__item'>
         <div className='profile-card-inf__title'>4561</div>
         <div className='profile-card-inf__txt'>average_likes</div>
        </div>
        
       </div>
    
       <div className='profile-card-ctr'>
        <button className='profile-card__button button--blue js-message-btn'><EditProfile/> </button>
        <button className='profile-card__button button--orange'  onClick={()=>handleDelete()}>Delete</button>
        <button  className='profile-card__button button--blue js-message-btn' onClick={logout}>Logout</button>
       </div> 
   
      
     </div>
     
     </div>
      </div> 
   </section>
     
      
    
  
  )
}

export default Profile
