     try {
      const res = await  axios.post('/api/user/register', userData);
      dispatch({type : REGISTER_SUCCESS , payload: res.data});
      nav('/login');
     } catch (error) {
        console.log(userData);
        dispatch({type : REGISTER_FAIL , payload: error})
     } 
 };


