import {useEffect} from 'react';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import {useForm} from "react-hook-form";
import { login as loginAction } from '../action/userAction';
import './loginadmin.css'
const LoginAdmin = () => { 
   const dispatch = useDispatch();
   const nav = useNavigate();
    const {register, handleSubmit} = useForm();
   const {isAuth, errors} =  useSelector(state=>state.user)
    useEffect(()=>{
if (isAuth) nav('/dashbord') 
    }, [isAuth,nav]);
    const loginUser = (data)=>{
      dispatch(loginAction(data));
  };
    return(
        <div >
        <Container>
  <Row>
    <Col></Col>
    <Col>2 of 2</Col>
    <Col></Col>
  </Row>
         <div className="log">
       
     
     
     
     

      <form onSubmit={handleSubmit(loginUser)}>  
      <div style={{'text-align':'center'}}>      <img className='avatar'  src="https://pngset.com/images/avatar-icon-red-clipart-download-red-video-camera-icon-person-face-text-logo-transparent-png-878638.png"/>
</div>
 <h1> Login Here </h1>
                  <label  >Email </label>
        <input type='email' name='email'  {...register("email")} placeholder='email' />
       
        <br/>
                  <label className="form__label" > Password </label>
        <input type='password' name='password' {...register("password")} placeholder='password' />
     
        
       
        
        {errors && <p>{errors}</p>} 
      <button style={{ backgroundColor:"blue"}} > Login </button>
      
   
   
    </form>
    
  
  
 
 
 
 
 
 
 
 
 
 
  
  
   
   
   </div>
   </Container>
    </div>
    
    )
    }

export default LoginAdmin
