import React,{ useRef ,useState} from 'react'
import emailjs from '@emailjs/browser';
import './contact.css'
const Contact = () => {
    const [result, setResult] = useState(false)
    const form = useRef();

    const sendEmail = (e) => {
      e.preventDefault();
  
      emailjs.sendForm('service_zjl90jd', 'template_6l8c8b9', form.current, 'R4hpM-oPlIXcXwpf3')
        .then((result) => {
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        });
        setResult(true)
        e.target.reset()
        setTimeout(()=>setResult(false),5000)
    };

  return (
    <section className='contact'>
<div className='content'>
<h2>CONTACTER NOUS </h2>
<p>For all enquiries, please email us using the form bellow.</p>
</div>
<div className='containeer'>
<div className='contactInfo'>
<div className='box'>
<div className='icon'> <i class="fa fa-map-marker" aria-hidden="true"></i>  </div>
  <div className='text'>
    <h3>Address</h3>
    <p>13.Rue Khaled Ibn Walid,<br/> Sousse,Tunisia, <br/> 4060 </p>
 
</div>
</div>
  <div className='box'>
<div className='icon'> </div>
  <div className='text'>
    <h3>Email</h3>
    <p> influencer@gmail.com </p>
  </div>
</div>
  <div className='box'>
<div className='icon'>  </div>
  <div className='text'>
    <h3>Phone</h3>
    <p>+21652718955 </p>
  </div>


</div>
</div>
</div>




         <form ref={form} onSubmit={sendEmail}>
      <label>Name</label>
      <input type="text" name="name" />
      <label>Email</label>
      <input type="email" name="email" />
      <label>Message</label>
      <textarea name="message" />
      <input type="submit" value="Send" />
     { result ? <h1>Message envoyé</h1> : null}
    </form>
    
    </section>
  )
}

export default Contact;
