import {useEffect} from 'react';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import {useForm} from "react-hook-form";
import { register as loginAction } from '../action/customerAction';
import './register.css';
const Login = () => { 
   const dispatch = useDispatch();
   const nav = useNavigate();
    const {register, handleSubmit} = useForm();
    const loginCustomer = (data)=>{
        dispatch(loginAction(data));
    };
    const {isAuth} =  useSelector(state=>state.customer.customerInfo)
//     useEffect(()=>{
// if (isAuth) nav('/login') 
//     }, [isAuth,nav]);
    return(
        <div>
        <form onSubmit={handleSubmit(loginCustomer)}>
        <nav className="bg-dark navbar-dark navbar">
            <div className="row col-12 d-flex justify-content-center text-white">
            <h3 style={{textAlign: "center"}}>Registration</h3>
            </div>
            </nav>
            <div className="form">
          <div className="form-body">
              
        <div className="email">
                  <label className="form__label" >Email </label>
        <input type='email' name='email'  {...register("Email")} placeholder='email' />
        </div>
        <div className="confirm-password">
                  <label className="form__label" > Password </label>
        <input type='password' name='password' {...register("Password")} placeholder='password' />
        </div>
        
        <div className="footer">
      <button style={{ backgroundColor:"blue"}} > Login </button> 
      </div> 
    </div>
    </div>
    </form>
    </div>
    
    )
    }
export default Login;
