import axios from 'axios';
import { ADD_USER_FAILED, ADD_USER_LODING, ADD_USER_SUCESS } from './types';
export const addNewUser = (inputs) =>dispatch=> { 
    dispatch({type : ADD_USER_LODING})
    axios.post('http://localhost:5022/api/customer', inputs)
    .then((res) => dispatch({type : ADD_USER_SUCESS, payload: res.data}))
    .catch((err) => dispatch({type : ADD_USER_FAILED, payload: err}));
 };
