import {useEffect} from 'react';
import { useDispatch, useSelector} from 'react-redux';
import { getCustomer } from '../action/customerAction';
import Table from 'react-bootstrap/Table'
const ListCustomer = () => {
  const dispatch = useDispatch();
  const {customerList} = useSelector((state)=>state.customer);
  useEffect(()=> {
    dispatch(getCustomer())
  },[dispatch]);
  
  return (
    <div>
    <Table style={{'marginTop':"60px" }} striped bordered hover  variant="dark">
  <thead style={{'padding': "15px"}}>
    <tr>
      <th>Edit</th>
      <th>First Name</th>
      <th>Last Name</th>
      <th>Username</th>
      <th>Phone</th>
      <th>Address</th>
      <th>Country</th>
      <th>Delete</th>
    </tr>
    </thead>
    {customerList.map(el=>(
        <>
        
       

    <tbody>
    <tr>
      
      <td>{el.firstName}</td>
      <td>{el.lastName}</td>
      <td>{el.email}</td>
      <td>{el.phone}</td>
      <td>{el.address}</td>
      <td>{el.country}</td>
      <td></td>
    </tr>
    </tbody>
  
    </>
    ))};
  </Table>

    
     
     

    </div>
  )
};


export default ListCustomer
