import { REGISTER_FAIL, REGISTER_SUCCESS, LOGIN_SUCCESS, LOGIN_FAIL } from "../action/types";

const initState = {
    customerInfo:{},
    token:local,
    errors:null,
    isAuth:false,
};

const customerReducer = (state = initState,{type,payload}) => { 
    switch(type){
        case LOGIN_FAIL:
        case REGISTER_FAIL:
            return{...state, errors : payload }
            case REGISTER_SUCCESS:
                return{ 
                    ...state,
                    customerInfo: payload.newCustomer,
                    errors:null,
         };
         case LOGIN_SUCCESS:
             localStorage.setItem('token',payload.token);
             localStorage.setItem('isAuth',true);

            return{ 
                ...state,
                customerInfo: payload.existCustomer,
                 token: payload.token,
                 isAuth: true,
                errors:null,
     };
        default : 
        return state
    }
 };
 export default customerReducer;
